async function formatLegendFont() {
  await Excel.run(async (context) => {
    const sheet = context.workbook.worksheets.getItem("Sample");

    let chart = sheet.charts.getItemAt(0);
    chart.legend.visible = true;

    // Format the legend font.
    let font = chart.legend.format.font;
    font.bold = true;
    font.color = "red";
    font.italic = true;
    font.size = 15;
    font.name = "Calibri";
    font.underline = "Single";

    await context.sync();
  });
}

async function setup() {
  await Excel.run(async (context) => {
    context.workbook.worksheets.getItemOrNullObject("Sample").delete();
    const sheet = context.workbook.worksheets.add("Sample");

    let expensesTable = sheet.tables.add("A1:E1", true);
    expensesTable.name = "SalesTable";

    expensesTable.getHeaderRowRange().values = [["Product", "Qtr1", "Qtr2", "Qtr3", "Qtr4"]];

    expensesTable.rows.add(null, [
      ["Frames", 5000, 7000, 6544, 4377],
      ["Saddles", 400, 323, 276, 651],
      ["Brake levers", 12000, 8766, 8456, 9812],
      ["Chains", 1550, 1088, 692, 853],
      ["Mirrors", 225, 600, 923, 544],
      ["Spokes", 6005, 7634, 4589, 8765]
    ]);

    sheet.getUsedRange().format.autofitColumns();
    sheet.getUsedRange().format.autofitRows();

    createChart(context);
    addVerticalAxisLabel(context);
    sheet.activate();

    await context.sync();
  });
}

function createChart(context: Excel.RequestContext) {
  const sheet = context.workbook.worksheets.getItem("Sample");
  const salesTable = sheet.tables.getItem("SalesTable");

  const dataRange = salesTable.getDataBodyRange();

  let chart = sheet.charts.add("ColumnClustered", dataRange, Excel.ChartSeriesBy.columns);

  chart.setPosition("A15", "F30");
  chart.title.text = "Quarterly sales chart";
  chart.legend.position = "Right";
  chart.legend.format.fill.setSolidColor("white");
  chart.dataLabels.format.font.size = 15;
  chart.dataLabels.format.font.color = "black";
  let points = chart.series.getItemAt(0).points;
  points.getItemAt(0).format.fill.setSolidColor("pink");
  points.getItemAt(1).format.fill.setSolidColor("indigo");
}

function addVerticalAxisLabel(context: Excel.RequestContext) {
  const sheet = context.workbook.worksheets.getItem("Sample");

  let chart = sheet.charts.getItemAt(0);
  let axis = chart.axes.valueAxis;
  axis.displayUnit = "Thousands";
}
