Enum Excel.DataValidationOperator { between, equalTo, greaterThan, greaterThanOrEqualTo, lessThan, lessThanOrEqualTo, notBetween, notEqualTo }

async function addPositiveNumberDataValidationRule() {
  await Excel.run(async (context) => {
    const currentWorksheet = context.workbook.worksheets.getActiveWorksheet();
    const range = currentWorksheet.getRange("A1:D4");

    // clear previous data validation rule
    range.dataValidation.clear();

    let greaterThanZeroRule = {
      wholeNumber: {
        formula1: 0,
        operator: Excel.DataValidationOperator.greaterThan
      }
    };

    range.dataValidation.rule = greaterThanZeroRule;

    range.dataValidation.prompt = {
      message: "Please enter a positive number.",
      showPrompt: true,
      title: "Positive numbers only."
    };

    range.dataValidation.errorAlert = {
      message: "Sorry, only positive numbers are allowed",
      showAlert: true,
      style: "Stop",
      title: "Negative Number Entered"
    };

    await context.sync();
  });
}

async function removeDataValidatonRuleOnRange() {
  await Excel.run(async (context) => {
    const currentWorksheet = context.workbook.worksheets.getActiveWorksheet();
    const range = currentWorksheet.getRange("A1:D4");

    // remove data validation rule
    range.dataValidation.clear();
    await context.sync();
  });
}
