// Get the data values of the first pivoTable in the current worksheet.
async function getPivotTableDataInWorksheet() {
  await Excel.run(async (context) => {
    const pivotTables = context.workbook.worksheets.getActiveWorksheet().pivotTables;
    pivotTables.load("items");
    await context.sync();

    const firstPivotTable = pivotTables.items[0];
    firstPivotTable.load("layout");
    await context.sync();

 // Get the range of the pivoTable
    const pivotLayout = firstPivotTable.layout;
    const pivotTableRange = pivotLayout.getRange();
    pivotTableRange.load("values");
    await context.sync();

  // Get the values of the range
    const pivotTableValues = pivotTableRange.values;
    await context.sync();
    console.log("values: "+ pivotTableValues);
  });
}

// Get all pivoTable's names in the workbook
async function getPivotTableNamesInWorkbook() {
  await Excel.run(async (context) => {
    // Get the names of all the PivotTables in the workbook.
    const pivotTables = context.workbook.pivotTables;
    pivotTables.load("name");
    await context.sync();

    // Display the names in the console.
    console.log("PivotTables in the workbook:");
    pivotTables.items.forEach((pivotTable) => {
      console.log(`	${pivotTable.name}`);
    });
  });
}
}
