async function formatTable() {
  await Excel.run(async (context) => {
    const sheet = context.workbook.worksheets.getItem("Sample");
    const firstTable = sheet.tables.getItemAt(0);

    // set the table header's color
    firstTable.getHeaderRowRange().format.fill.color = "#C70039";
    // set the table body's color
    firstTable.getDataBodyRange().format.fill.color = "#DAF7A6";
    // set the second table row's color
    firstTable.rows.getItemAt(1).getRange().format.fill.color = "#FFC300";
    // set the first table column's color
    firstTable.columns.getItemAt(0).getDataBodyRange().format.fill.color = "#FFA07A";

    await context.sync();
  });
}
