async function getTableData() {
  await Excel.run(async (context) => {
    const sheet = context.workbook.worksheets.getActiveWorksheet();
    // Get the first table of the worksheet
    const fisrtTable = sheet.tables.getItemAt(0);

    // Get the table's header's value of the fisrtTable
    const headerRange = fisrtTable.getHeaderRowRange().load("values");
    await sheet.context.sync();
    const headerValues = headerRange.values;
    console.log(headerValues);

    // Get the table's value of the fisrtTable
    const bodyRange = fisrtTable.getDataBodyRange().load("values");
    await context.sync();
    const bodyValues = bodyRange.values;
    console.log(bodyValues);

    // Get the first colomn's value of the fisrtTable
    const columnRange = fisrtTable.columns
      .getItemAt(0)
      .getDataBodyRange()
      .load("values");
    await context.sync();
    const firstColumnValues = columnRange.values;
    console.log(firstColumnValues);

    // Get the first row's value of the fisrtTable
    const rowRange = fisrtTable.rows.getItemAt(0).load("values");
    await context.sync();
    const firstRowValues = rowRange.values; 
    console.log(firstRowValues);
  });
}
