}

async function selectShapes() {
  // Selects the first two shapes on slide 1.
  await PowerPoint.run(async (context) => {
    const slide1 = context.presentation.slides.getItemAt(0); // get the first slide
    slide1.load("shapes");
    await context.sync();
    const shapes = slide1.shapes;
    const shape1 = shapes.getItemAt(0); // get the first shape
    const shape2 = shapes.getItemAt(1); // second shape
    shape1.load("id");
    shape2.load("id");
    await context.sync();
    slide1.setSelectedShapes([shape1.id, shape2.id]); // select the two shape use id
    await context.sync();
  });
}

async function changeShapeFill() {
  // Changes the selected shapes fill color to red.
  await PowerPoint.run(async (context) => {
    const shapes = context.presentation.getSelectedShapes(); // active selected shapes
    shapes.load("items");
    await context.sync();
    shapes.items.map((shape) => {
      // change shape fill
      shape.fill.foregroundColor = "yellow";
      shape.fill.transparency = 0.5;
      shape.fill.setSolidColor("red");
    });
    await context.sync();
  });
}

async function createTextBox() {
  // adds a text box to the shape collection, while specifying its text,
  // location, and size. Then it names the text box.
  await PowerPoint.run(async (context) => {
    const shapes = context.presentation.slides.getItemAt(0).shapes; // shapes on the first slide
    const textbox = shapes.addTextBox("Hello!", {
      left: 100,
      top: 300,
      height: 300,
      width: 450
    });
    textbox.name = "Textbox";

    return context.sync();
  });
}

async function createShapes() {
  // Creates random shapes on the selected slide.
  await PowerPoint.run(async (context) => {
    const currentSlide = context.presentation.getSelectedSlides().getItemAt(0); // get the first active selected slide
    for (let i = 0; i < 20; i++) {
      const rectangle = currentSlide.shapes.addGeometricShape(PowerPoint.GeometricShapeType.rectangle); // add a rectangle shape
      rectangle.height = 180;
      rectangle.width = 160;
      rectangle.left = 90;
      rectangle.top = 60;
      rectangle.fill.foregroundColor = "yellow";
    }
    await context.sync();
  });
}

async function addLine() {
  // adds a line to the shape collection, specifying its
  // start and end points.
  await PowerPoint.run(async (context) => {
    const shapes = context.presentation.slides.getItemAt(0).shapes;

    // For a line, left and top are the coordinates of the start point,
    // while height and width are the coordinates of the end point.
    const line = shapes.addLine(PowerPoint.ConnectorType.straight,
      {
        left: 400,
        top: 200,
        height: 20,
        width: 150
      });
    line.name = "StraightLine";

    await context.sync();
  });
}

async function changeShapePositionSize() {
  // change shape position
  await PowerPoint.run(async (context) => {
    const shapes = context.presentation.getSelectedShapes();
    shapes.load("items");
    await context.sync();
    shapes.items.map((shape) => {
      shape.load("width, height"); // load width, height property
    });

    await context.sync();
    shapes.items.map((shape) => {
      shape.left = 15;
      shape.top = 20;
      shape.height = 50;
      shape.width = 100;
    });
    await context.sync();
  });
}
