async function insertEndnote() {
  // Sets a endnote on the selected content.
  await Word.run(async (context) => {
    const text = "a endnote";
    const endnote = context.document.getSelection().insertEndnote(text);
    await context.sync();

    console.log("Inserted endnote.");
  });
}

async function getReference() {
  // Selects the endnote's reference mark in the document body.
  await Word.run(async (context) => {
    const endnotes = context.document.body.endnotes;
    endnotes.load("items/reference");
    await context.sync();

    // first endnote
    const item = endnotes.items[0];
    const reference = item.reference;
    reference.select();
    await context.sync();

    console.log(`The first endnote's reference is selected.`);
  });
}

async function getEndnoteType() {
  // Gets the referenced note's item type and body type, which are both "Endnote".
  await Word.run(async (context) => {
    const endnotes = context.document.body.endnotes;
    endnotes.load("items");
    await context.sync();

    // first endnotes
    const item = endnotes.items[0];
    console.log(`Note type of endnote 0: ${item.type}`);

    item.body.load("type");
    await context.sync();

    console.log(`Body type of note: ${item.body.type}`);
  });
}

async function getEndnoteBody() {
  // Gets the text of the referenced endnote.
  await Word.run(async (context) => {
    const endnotes = context.document.body.endnotes;
    endnotes.load("items/body");
    await context.sync();

    const endnoteBody = endnotes.items[0].body.getRange();
    endnoteBody.load("text");
    await context.sync();

    console.log(`Text of endnote 0: ${endnoteBody.text}`);
  });
}

async function getNextEndnote() {
  // Selects the next endnote in the document body.
  await Word.run(async (context) => {
    const endnotes = context.document.body.endnotes;
    endnotes.load("items/reference");
    await context.sync();

    //get the first endnote's next endnote
    const reference = endnotes.items[0].getNext().reference;
    reference.select();
    console.log("Selected is the next endnote");
  });
}

async function deleteEndnote() {
  // Deletes this referenced endnote.
  await Word.run(async (context) => {
    const endnotes = context.document.body.endnotes;
    endnotes.load("items");
    await context.sync();

    endnotes.items[0].delete();
    await context.sync();

    console.log("Endnote deleted.");
  });
}
async function getFirstEndnote() {
  // Gets the first endnote in the document body and select its reference mark.
  await Word.run(async (context) => {
    const reference = context.document.body.endnotes.getFirst().reference;
    reference.select();
    console.log("The first endnote is selected.");
  });
}

async function getEndnotesFromBody() {
  // Gets the endnotes in the document body.
  await Word.run(async (context) => {
    const endnotes = context.document.body.endnotes;
    endnotes.load("length");
    await context.sync();

    console.log("Number of endnotes in the document body: " + endnotes.items.length);
  });
}
