async function getSelectedFieldAndUpdate() {
  // Gets and updates the first field in the selection.
  await Word.run(async (context) => {
    var field = context.document.getSelection().fields.getFirstOrNullObject();
    field.load(["code", "result", "type", "locked"]);

    await context.sync();

    if (field.isNullObject) {
      console.log("No field in selection.");
    } else {
      console.log(
        "Before updating:",
        "Code of first field: " + field.code,
        "Result of first field: " + JSON.stringify(field.result)
      );

      field.updateResult();
      field.select();
      await context.sync();

      field.load(["code", "result"]);
      await context.sync();

      console.log(
        "After updating:",
        "Code of first field: " + field.code,
        "Result of first field: " + JSON.stringify(field.result)
      );
    }
  });
}
