async function getTableAlignment() {
  // Gets alignment details about the first table in the document.
  await Word.run(async (context) => {
    const firstTable = context.document.body.tables.getFirst();
    firstTable.load(["alignment", "horizontalAlignment", "verticalAlignment"]);
    await context.sync();

    console.log(
      `Details about the alignment of the first table:`,
      `- Alignment of the table within the containing page column: ${firstTable.alignment}`,
      `- Horizontal alignment of every cell in the table: ${firstTable.horizontalAlignment}`,
      `- Vertical alignment of every cell in the table: ${firstTable.verticalAlignment}`
    );
  });
}

async function getTableRowAlignment() {
  // Gets content alignment details about the first row of the first table in the document.
  await Word.run(async (context) => {
    const firstTable = context.document.body.tables.getFirst();
    const firstTableRow = firstTable.rows.getFirst();
    firstTableRow.load(["horizontalAlignment", "verticalAlignment"]);
    await context.sync();

    console.log(
      `Details about the alignment of the first table's first row:`,
      `- Horizontal alignment of every cell in the row: ${firstTableRow.horizontalAlignment}`,
      `- Vertical alignment of every cell in the row: ${firstTableRow.verticalAlignment}`
    );
  });
}

async function getTableCellAlignment() {
  // Gets content alignment details about the first cell of the first table in the document.
  await Word.run(async (context) => {
    const firstTable = context.document.body.tables.getFirst();
    const firstCell = firstTable.getCell(0, 0);
    firstCell.load(["horizontalAlignment", "verticalAlignment"]);
    await context.sync();

    console.log(
      `Details about the alignment of the first table's first cell:`,
      `- Horizontal alignment of the cell's content: ${firstCell.horizontalAlignment}`,
      `- Vertical alignment of the cell's content: ${firstCell.verticalAlignment}`
    );
  });
}
