async function getTableBorder() {
  // Gets border details about the first table in the document.
  await Word.run(async (context) => {
    const firstTable = context.document.body.tables.getFirst();
    const borderLocation = Word.BorderLocation.top;
    const border = firstTable.getBorder(borderLocation);
    border.load(["type", "color", "width"]);
    await context.sync();

    console.log(
      `Details about the ${borderLocation} border of the first table:`,
      `- Color: ${border.color}`,
      `- Type: ${border.type}`,
      `- Width: ${border.width} points`
    );
  });
}

async function getTableRowBorder() {
  // Gets border details about the first row of the first table in the document.
  await Word.run(async (context) => {
    const firstTable = context.document.body.tables.getFirst();
