async function getCount() {
  // Gets the number of styles.
  await Word.run(async (context) => {
    const styles = context.document.getStyles();
    const count = styles.getCount();
    await context.sync();
  });
}

async function addStyle() {
  // Adds a new style.
  await Word.run(async (context) => {
    const newStyleName = "newStyleName"; //Replaced by your own new style name
    const style = context.document.getStyles().getByNameOrNullObject(newStyleName);
    if (!style.isNullObject) {
      return;
    }
    context.document.addStyle(newStyleName, Word.StyleType.paragraph);
    await context.sync();
  });
}

