async function applyStyle() {
  // Applies the specified style to a paragraph.
  await Word.run(async (context) => {
    const styleName = "styleName"; //Replaced by your own style name
    const style = context.document.getStyles().getByNameOrNullObject(styleName);
    style.load();
    await context.sync();

    if (style.isNullObject || style.type != Word.StyleType.paragraph) {
      return;
    } else {
      const body = context.document.body;
      body.clear();
      body.insertParagraph(
        "A new paragraph",
        "Start"
      );
      const paragraph = body.paragraphs.getFirst();
      paragraph.style = style.nameLocal;
      await context.sync();
    }
  });
}

async function setFontAndParagraphFormatProperties() {
  // Updates font properties (e.g., color, size) of the specified style.
  await Word.run(async (context) => {
    const styleName = "styleName"; //Replaced by your own style name
    const style = context.document.getStyles().getByNameOrNullObject(styleName);
    if (style.isNullObject) {
      return;
    } else {
     style.paragraphFormat.alignment = Word.Alignment.centered;
    style.paragraphFormat.leftIndent = 30;
      style.font.color = "#FF0000";
      style.font.size = 20;
      await context.sync();
    }
  });
}

async function deleteStyle() {
  // Deletes the custom style.
  await Word.run(async (context) => {
    const styleName = "styleName"; //Replaced by customed style name to delete
    const style = context.document.getStyles().getByNameOrNullObject(styleName);
    style.load();
    await context.sync();
    if (style.isNullObject) {
      return;
    } else {
      style.delete();
    }
  });
}
